    speed: 1000,
    adaptiveHeight: true,
    prevArrow:
      '<button type="button" class="slick-prev"><img src="icons/promo_left.png"/></button>',
    nextArrow:
      '<button type="button" class="slick-next"><img src="icons/promo_right.png"/></button>',
